import { Component } from '@angular/core';
import { UniversalService } from '../services/universal.service';
import { Router } from '@angular/router';
import { Ucenik } from '../models/ucenik';

@Component({
  selector: 'app-registracija-ucenik',
  templateUrl: './registracija-ucenik.component.html',
  styleUrls: ['./registracija-ucenik.component.css']
})
export class RegistracijaUcenikComponent {

  constructor(private universalService : UniversalService, private router : Router) {}

  korIme : string = ""
  lozinka : string = ""
  bezbedonosnoPitanje : string = ""
  bezbedonosniOdgovor : string = ""
  ime : string = ""
  prezime : string = ""
  pol : string = ""
  adresa : string = ""
  telefon : string = ""
  email : string = ""
  slika : string = "../../assets/defaultProfilePicture2.jpg"
  tipSkole : string = ""
  razred : number = 0

  errKorIme : string = "" //
  errLozinka : string = ""
  errBezbedonosnoPitanje : string = "" //
  errBezbedonosniOdgovor : string = "" //
  errIme : string = "" //
  errPrezime : string = "" //
  errPol : string = "" //
  errAdresa : string = "" //
  errTelefon : string = "" //
  errEmail : string = "" //
  errSlika : string = "" //
  errTipSkole : string = "" //
  errRazred : string = ""

  errMsg : string = ""

  register() {

    if (this.korIme == "") this.errKorIme = "Niste uneli korisnicko ime. "
    if (this.bezbedonosnoPitanje == "") this.errBezbedonosnoPitanje = "Niste uneli bezbedonosno pitanje. "
    if (this.bezbedonosniOdgovor == "") this.errBezbedonosniOdgovor = "Niste uneli bezbedonosni odgovor. "
    if (this.ime == "") this.errIme = "Niste uneli ime. "
    if (this.prezime == "") this.errPrezime = "Niste uneli prezime. "
    if (this.pol == "") this.errPol = "Niste odabrali pol. "
    if (this.adresa == "") this.errAdresa = "Niste uneli adresu. "
    if (this.telefon == "") this.errTelefon = "Niste uneli telefon. "
    if (this.email == "") this.errEmail = "Niste uneli email. "
    if (this.slika == "") this.errSlika = "Niste odabrali sliku. "
    if (this.tipSkole == "") this.errTipSkole = "Niste odabrali tip skole. "

    let regex = /^/
    if (!regex.test(this.lozinka)) this.errLozinka = "Neispravna lozinka. "

    if (this.tipSkole != "osnovna" && this.razred > 4) this.errRazred = "Neispravno unet razred. "


    let ucenik = new Ucenik()
    ucenik.korIme = this.korIme;
    ucenik.lozinka = this.lozinka;
    ucenik.bezbedonosnoPitanje = this.bezbedonosnoPitanje;
    ucenik.bezbedonosniOdgovor = this.bezbedonosniOdgovor;
    ucenik.ime = this.ime;
    ucenik.prezime = this.prezime;
    ucenik.pol = this.pol;
    ucenik.adresa = this.adresa;
    ucenik.telefon = this.telefon;
    ucenik.email = this.email;
    ucenik.slika = this.slika;
    ucenik.tipSkole = this.tipSkole;
    ucenik.razred = this.razred;

    this.universalService.register(ucenik).subscribe(
      data => {
        if (data != null) {
          this.router.navigate([''])
        }
        else {
          console.log("Greska. ")
        }
      }
    )
  }

}
