import { Component, OnInit, Predicate } from '@angular/core';
import {Chart, registerables} from 'chart.js';
import { UniversalService } from '../services/universal.service';
import { Router } from '@angular/router';
import { Predmet } from '../models/predmet';
import { Korisnik } from '../models/korisnik';
import { Nastavnik } from '../models/nastavnik';
import { Ucenik } from '../models/ucenik';
import { Cas } from '../models/cas';
import { Predlog } from '../models/predlog';


@Component({
  selector: 'app-admin',
  templateUrl: './admin.component.html',
  styleUrls: ['./admin.component.css']
})
export class AdminComponent implements OnInit {

  constructor(private universalService: UniversalService, private router: Router) {}

  korisnik : Korisnik = new Korisnik()

  sviPredmeti : Predmet[] = []
  sviPredmetiNaziv : string[] = []
  brojNastavnikaNaPredmetuArr : number[] = []

  sviNastavnici : Nastavnik[] = []
  sviAktivniNastavnici : Nastavnik[] = []
  polNastavnici : number[] = [0, 0]
  flagAktivanValues : number[] = [0, 0, 0]

  angazovanost2023 : number[][] = []

  sviUcenici : Ucenik[] = []
  polUcenici : number[] = [0, 0]

  sviCasovi : Cas[] = []
  potvrda : number[] = [0, 0, 0] // po labelama: 0-odbijen, 1-prihvacen, 2-nije jos potvrdjen

  zahteviReg : Nastavnik[] = []

  noviPredmet : string = ""
  sviPredlozi : Predlog[] = []

  odrzanihPon : number = 0
  odrzanihUto : number = 0
  odrzanihSre : number = 0
  odrzanihCet : number = 0
  odrzanihPet : number = 0
  odrzanihSub : number = 0
  odrzanihNed : number = 0
  prosekPoDanu : number[] = [0, 0, 0, 0, 0, 0, 0]

  chartHistogram1 : any
  chartHistogram2 : any
  chartPie1 : any
  chartPie2 : any
  chartPie3 : any
  chartPie4 : any

  ngOnInit(): void {

    /*
    window.addEventListener('load', () => {
      this.generisanjeHistograma()
      this.generisanjePie1()
      this.generisanjePie2()
      this.generisanjePie3()
      this.generisanjePie4()
      this.generisanjeHistograma2()
    });*/

    let k = localStorage.getItem("logovaniKorisnik")
    if (k != null) { 
      this.korisnik = JSON.parse(k)

      this.dohvatiSvePredmete()
      //this.generisanjeHistograma()
      this.dohvatiSveNastavnike()
      //this.generisanjePie1()
      this.dohvatiSveUcenike()
      //this.generisanjePie2()
      this.dohvatiSveCasove()
      //this.generisanjePie3()
      //this.generisanjePie4()
      //this.generisanjeHistograma2()

      this.dohvatiZahteveNastavnika()
      this.dohvatiSvePredloge()


      this.generisanjeHistograma()
      this.generisanjePie1()
      this.generisanjePie2()
      this.generisanjePie3()
      this.generisanjePie4()
      this.generisanjeHistograma2()

    }
  }

  odjava(event: Event) {
    event.preventDefault();
    localStorage.removeItem("logovaniKorisnik");
    this.router.navigate(['']);
  }

  refreshData() {

    this.noviPredmet = ""

    this.odrzanihPon = 0
    this.odrzanihUto = 0
    this.odrzanihSre = 0
    this.odrzanihCet = 0
    this.odrzanihPet = 0
    this.odrzanihSub = 0
    this.odrzanihNed = 0
    this.prosekPoDanu = [0, 0, 0, 0, 0, 0, 0]

    this.chartHistogram1.destroy()
    this.chartHistogram2.destroy()
    this.chartPie1.destroy()
    this.chartPie2.destroy()
    this.chartPie3.destroy()
    this.chartPie4.destroy()

    this.polNastavnici = [0, 0]
    this.flagAktivanValues = [0, 0, 0]
    this.polUcenici = [0, 0]
    this.potvrda = [0, 0, 0]

    this.sviNastavnici = []
    this.sviAktivniNastavnici = []

    this.sviPredmeti = []
    this.sviPredmetiNaziv = []
    this.brojNastavnikaNaPredmetuArr = []

    this.sviUcenici = []
    this.sviCasovi = []
    this.zahteviReg = []
    this.sviPredlozi = []

    this.dohvatiSvePredmete()
    this.generisanjeHistograma()
    this.dohvatiSveNastavnike()
    this.generisanjePie1()
    this.dohvatiSveUcenike()
    this.generisanjePie2()
    this.dohvatiSveCasove()
    this.generisanjePie3()
    this.generisanjePie4()
    this.generisanjeHistograma2()

    this.dohvatiZahteveNastavnika()
    this.dohvatiSvePredloge()
  }

  dodajPredmetAdmin() {
    let noviPredmetIndex = this.sviPredmeti.length;
    this.universalService.dodajPredmetAdmin(this.noviPredmet, noviPredmetIndex).subscribe(
      data => {
        if (data != null) {
          this.refreshData()
        }
        else {
          console.log("Greska. ")
        }
      }
    )
  }

  prihvatiPredlog(p: Predlog) {
    let np = new Predmet()
    np.naziv = p.naziv;
    np.index = this.sviPredmeti.length;
    this.universalService.prihvatiPredlog(p, np).subscribe(
      data => {
        if (data != null) {
          this.refreshData()
        }
        else {
          console.log("Greska. ")
        }
      }
    )
  }

  odbijPredlog(p: Predlog) {
    this.universalService.odbijPredlog(p).subscribe(
      data => {
        if (data != null) {
          this.refreshData()
        }
        else {
          console.log("Greska. ")
        }
      }
    )
  }

  dohvatiSvePredloge() {
    this.universalService.dohvatiSvePredloge().subscribe(
      data => {
        if (data != null) {
          this.sviPredlozi = data
        }
        else {
          console.log("Greska. ")
        }
      }
    )
  }

  dohvatiZahteveNastavnika() {
    this.universalService.dohvatiZahteveNastavnika().subscribe(
      data => {
        if (data != null) {
          this.zahteviReg = data
        }
        else {
          console.log("Greska. ")
        }
      }
    )
  }

  prihvatiZahtev(n: Nastavnik) {
    this.universalService.prihvatiZahtev(n).subscribe(
      data => {
        if (data != null) {
          this.refreshData()
        }
        else {
          console.log("Greska. ")
        }
      }
    )
  }

  odbijZahtev(n: Nastavnik) {
    this.universalService.odbijZahtev(n).subscribe(
      data => {
        if (data != null) {
          this.refreshData()
        }
        else {
          console.log("Greska. ")
        }
      }
    )
  }

  azurirajPodatkeNastavnika(n: Nastavnik, event: Event) {
    event.preventDefault()
    localStorage.setItem("logovaniKorisnik",JSON.stringify(this.korisnik))
    localStorage.setItem("nastavnikZaAzuriranje", JSON.stringify(n))
    this.router.navigate(['azurirajNastavnika'])
  }

  dohvatiSveUcenike() {
    this.universalService.dohvatiSveUcenike().subscribe(
      data => {
        if (data != null) {
          this.sviUcenici = data

          for (let i = 0; i < this.sviUcenici.length; i++) {
            if (this.sviUcenici[i].pol == 'M') this.polUcenici[0]++;
            else this.polUcenici[1]++
          }
        }
        else {
          console.log("Greska. ")
        }
      }
    )
  }

   dohvatiSveNastavnike() {
    this.universalService.dohvSveNastavnike().subscribe(
      data => {
        if (data != null) {
          this.sviNastavnici = data

          for (let i = 0; i < this.sviNastavnici.length; i++) {
            if (this.sviNastavnici[i].pol == 'M') this.polNastavnici[0]++;
            else this.polNastavnici[1]++

            if (this.sviNastavnici[i].flagAktivan == 0) this.flagAktivanValues[0]++;
            else if (this.sviNastavnici[i].flagAktivan == 1) this.flagAktivanValues[1]++;
            else if (this.sviNastavnici[i].flagAktivan == 2) this.flagAktivanValues[2]++;

            if (this.sviNastavnici[i].flagAktivan == 1) this.sviAktivniNastavnici.push(this.sviNastavnici[i])

            if (this.sviNastavnici[i].flagAktivan == 1) {
              // provera koliko je casova drzao u 2023
              this.universalService.brojCasovaNastavnika2023(this.sviNastavnici[i].korIme).subscribe(
                data => {
                  if (data != null) {
                    // vratice array pomesecima koliko je casova drzao
                    this.angazovanost2023.push(data)
                  }
                  else {
                    console.log("Greska. ")
                  }
                }
              )
            }
          }

          // treba da sortiramo po angazovanosti i slice-ujemo samo prvih 10 najangazovanijih
          this.angazovanost2023.sort((a, b) => (
            a.reduce((accumulator, currentValue) => accumulator + currentValue, 0) > b.reduce((accumulator, currentValue) => accumulator + currentValue, 0)
            ) ? -1 : 1);

          this.angazovanost2023.slice(0, 10);
        }
        else {
          console.log("Greska. ")
        }
      }
    )
  }

  dohvatiSveCasove() {
    this.universalService.dohvatiSveCasove().subscribe(
      data => {
        if (data != null) {
          this.sviCasovi = data

          for (let i = 0; i < this.sviCasovi.length; i++) {
            if (this.sviCasovi[i].flagPotvrda == 0) this.potvrda[0]++;
            else if (this.sviCasovi[i].flagPotvrda == 1) this.potvrda[1]++;
            else this.potvrda[2]++;

            let dTemp = new Date(this.sviCasovi[i].datumVremeInput)
            if (this.sviCasovi[i].flagOdrzan == true && dTemp.getFullYear() == 2023) {

              if (dTemp.getDay() == 0) this.odrzanihNed++
              else if(dTemp.getDay() == 1) this.odrzanihPon++
              else if(dTemp.getDay() == 2) this.odrzanihUto++
              else if(dTemp.getDay() == 3) this.odrzanihSre++
              else if(dTemp.getDay() == 4) this.odrzanihCet++
              else if(dTemp.getDay() == 5) this.odrzanihPet++
              else if(dTemp.getDay() == 6) this.odrzanihSub++

            }
          }

          this.prosekPoDanu[0] = this.odrzanihPon / 52
          this.prosekPoDanu[1] = this.odrzanihUto / 52
          this.prosekPoDanu[2] = this.odrzanihSre / 52
          this.prosekPoDanu[3] = this.odrzanihCet / 52
          this.prosekPoDanu[4] = this.odrzanihPet / 52
          this.prosekPoDanu[5] = this.odrzanihSub / 52
          this.prosekPoDanu[6] = this.odrzanihNed / 52

        }
        else {
          console.log("Greska. ")
        }
      }
    )
  }

