import { Component, OnInit } from '@angular/core';
import { Ucenik } from '../models/ucenik';
import { Korisnik } from '../models/korisnik';
import { UcenikService } from '../services/ucenik.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-ucenik-profil',
  templateUrl: './ucenik-profil.component.html',
  styleUrls: ['./ucenik-profil.component.css']
})
export class UcenikProfilComponent implements OnInit {

  constructor(private ucenikService : UcenikService, private router : Router) {}

  uIme : string = ""
  uPrezime : string = ""
  uAdresa : string = ""
  uTelefon : string = ""
  uEmail : string = ""
  uSlika : string = "../../assets/defaultProfilePicture2.jpg"
  uTipSkole : string = ""
  uRazred : number = 0

  korisnik : Korisnik = new Korisnik()
  ucenik : Ucenik = new Ucenik()

  ngOnInit(): void {
    let logovani = localStorage.getItem("logovaniKorisnik")
    if (logovani != null) {
      this.korisnik = JSON.parse(logovani);
      this.dohvatiUcenika(this.korisnik)
    }
  }

  dohvatiUcenika(k : Korisnik) {
    this.ucenikService.dohvatiUcenika(k).subscribe(
      data => {
        if (data != null) {
          this.ucenik = data;
        }
      }
    )
  }

  izmeniIme() {
    this.ucenikService.izmeniIme(this.ucenik.korIme).subscribe(
      data => {
        if (data != null) {
          this.dohvatiUcenika(this.korisnik)
        }
        else {
          alert("Nije izmenjeno. ")
        }
      }
    )
  }

  izmeniPrezime() {
    this.ucenikService.izmeniPrezime(this.ucenik.korIme).subscribe(
      data => {
        if (data != null) {
          this.dohvatiUcenika(this.korisnik)
        }
        else {
          alert("Nije izmenjeno. ")
        }
      }
    )
  }

  izmeniAdresu() {
    this.ucenikService.izmeniAdresu(this.ucenik.korIme).subscribe(
      data => {
        if (data != null) {
          this.dohvatiUcenika(this.korisnik)
        }
        else {
          alert("Nije izmenjeno. ")
        }
      }
    )
  }

  izmeniTelefon() {
    this.ucenikService.izmeniTelefon(this.ucenik.korIme).subscribe(
      data => {
        if (data != null) {
          this.dohvatiUcenika(this.korisnik)
        }
        else {
          alert("Nije izmenjeno. ")
        }
      }
    )
  }

  izmeniEmail() {
    this.ucenikService.izmeniEmail(this.ucenik.korIme).subscribe(
      data => {
        if (data != null) {
          this.dohvatiUcenika(this.korisnik)
        }
        else {
          alert("Nije izmenjeno. ")
        }
      }
    )
  }

  izmeniSliku() {
    this.ucenikService.izmeniSliku(this.ucenik.korIme).subscribe(
      data => {
        if (data != null) {
          this.dohvatiUcenika(this.korisnik)
        }
        else {
          alert("Nije izmenjeno. ")
        }
      }
    )
  }

  izmeniTipSkole() {
    this.ucenikService.izmeniTipSkole(this.ucenik.korIme).subscribe(
      data => {
        if (data != null) {
          this.dohvatiUcenika(this.korisnik)
        }
        else {
          alert("Nije izmenjeno. ")
        }
      }
    )
  }

  izmeniRazred() {
    this.ucenikService.izmeniRazred(this.ucenik.korIme).subscribe(
      data => {
        if (data != null) {
          this.dohvatiUcenika(this.korisnik)
        }
        else {
          alert("Nije izmenjeno. ")
        }
      }
    )
  }


  profilStr(event : Event) {

