import { Component, OnInit, Predicate } from '@angular/core';
import {Chart, registerables} from 'chart.js';
import { UniversalService } from '../services/universal.service';
import { Router } from '@angular/router';
import { Predmet } from '../models/predmet';
import { Korisnik } from '../models/korisnik';
import { Nastavnik } from '../models/nastavnik';
import { Ucenik } from '../models/ucenik';


@Component({
  selector: 'app-admin',
  templateUrl: './admin.component.html',
  styleUrls: ['./admin.component.css']
})
export class AdminComponent implements OnInit {

  constructor(private universalService: UniversalService, private router: Router) {}

  korisnik : Korisnik = new Korisnik()

  sviPredmeti : Predmet[] = []
  sviPredmetiNaziv : string[] = []
  brojNastavnikaNaPredmetuArr : number[] = []

  sviNastavnici : Nastavnik[] = []
  sviUcenici : Ucenik[] = []

  ngOnInit(): void {
    let k = localStorage.getItem("logovaniKorisnik")
    if (k != null) {
      this.korisnik = JSON.parse(k)
      this.dohvatiSvePredmete()
      this.generisanjeHistograma()
    }
  }

  dohvatiSveNastavnike() {
    this.universalService.dohvSveNastavnike().subscribe(
      data => {
        if (data != null) {
          this.sviNastavnici = data
        }
        else {
          console.log("Greska. ")
        }
      }
    )
  }

  dohvatiSvePredmete() {
    this.universalService.dohvSvePredmete().subscribe(
      data => {
        if (data != null) {
          this.sviPredmeti = data

          for (let i = 0; i < this.sviPredmeti.length; i++) {
            this.universalService.brojNastavnikaNaPredmetu(this.sviPredmeti[i].naziv).subscribe(
              data2 => {
                if (data2 != null) {
                  this.brojNastavnikaNaPredmetuArr[i] = data2
                  this.sviPredmetiNaziv[i] = this.sviPredmeti[i].naziv
                }
                else {
                  console.log("Greska. ")
                }
              }
            )
          }

          //this.generisanjeHistograma()
        }
        else {
          console.log("Greska. ")
        }
      }
    )
  }

  generisanjeHistograma() {

    Chart.register(...registerables)

    let chart = new Chart("histogramCanvas", {
      type: 'bar',
      data: {
        labels: this.sviPredmetiNaziv, 
        datasets: [{
          label: 'Broj nastavnika po predmetu',
          data: this.brojNastavnikaNaPredmetuArr,
          backgroundColor: 'rgba(255, 99, 132, 0.2)', 
          borderColor: 'rgba(255, 99, 132, 1)', 
          borderWidth: 1
        }]
      },
      options: {
        scales: {
          y: {            beginAtZero: true,          },
        }
      }
    });
  }

  generisanjeHistogramaPie1() {

    Chart.register(...registerables)

    let chart = new Chart("pie1", {
      type: 'pie',
      data: {
        labels: ['M', 'Z'], 
        datasets: [{
          label: 'Raspodela Nastavnika po polu',
          data: [],
          backgroundColor: [
            'rgba(255, 99, 132, 0.2)',
            'rgba(54, 162, 235, 0.2)',
            'rgba(255, 206, 86, 0.2)',
            'rgba(75, 192, 192, 0.2)',
            'rgba(153, 102, 255, 0.2)',
          ],
          borderColor: [
            'rgba(255, 99, 132, 1)',
            'rgba(54, 162, 235, 1)',
            'rgba(255, 206, 86, 1)',
            'rgba(75, 192, 192, 1)',
            'rgba(153, 102, 255, 1)',
          ],
          borderWidth: 1
        }]
      },
      options: {
        scales: {
          y: {            beginAtZero: true,          },
        }
      }
    });
  }

}
