import { Component, OnInit } from '@angular/core';
import { UniversalService } from '../services/universal.service';
import { Nastavnik } from '../models/nastavnik';
import { Predmet } from '../models/predmet';
import { Ucenik } from '../models/ucenik';

@Component({
  selector: 'app-nereg-korisnik',
  templateUrl: './nereg-korisnik.component.html',
  styleUrls: ['./nereg-korisnik.component.css']
})
export class NeregKorisnikComponent implements OnInit {

  constructor(private universalService : UniversalService) {}

  brojUcenika : number = 0
  brojAktivnihNastavnika : number = 0
  brojCasovaPoslNedelja : number = 0
  brojCasovaPoslMesec : number = 0

  sviPredmeti : Predmet[] = []
  sviNastavnici : Nastavnik[] = []
  sviUcenici : Ucenik[] = []

  pretragaIme : string = ""
  pretragaPrezime : string = ""
  pretragaPredmet : string = ""

  errMsg : string = ""

  ngOnInit(): void {
    this.inicijalizujStranicu();
  }

  inicijalizujStranicu() {
    //this.dohvSveNastavnike();
    this.dohvSvePredmete();
    //this.izracunajBrojAktivnihNastavnika();
    //this.izracunajBrojUcenika();
  }

  dohvSveNastavnike() {
    this.universalService.dohvSveProfesore().subscribe(
      data => {
        if (data != null) {
          this.sviNastavnici = data;
        }
      }
    )
  }

  dohvSvePredmete() {
    this.universalService.dohvSvePredmete().subscribe(
      data => {
        if (data != null) {
          this.sviPredmeti = data;
        }
        else {
          this.errMsg = "Greska";
        }
      }
    )
  }

  izracunajBrojAktivnihNastavnika() {
    this.brojAktivnihNastavnika = 0

    this.sviNastavnici.forEach(element => {
      if (element.flagAktivan == 1) this.brojAktivnihNastavnika++;
    })
  }

  izracunajBrojUcenika() {
    this.brojUcenika = 0

    this.sviUcenici.forEach(element => {
      this.brojUcenika++;
    })
  }

  sortRastuce(promenljivaPoKojojSeSort : string) {
    /*
    if (promenljivaPoKojojSeSort == "ime") {
      this.sviNastavnici = this.sviNastavnici.slice().sort((a, b) => a.ime.localeCompare(b.ime))
    }
    */
  }

  sortOpadajuce(promenljivaPoKojojSeSort : string) {

  }

}
