import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Korisnik } from '../models/korisnik';
import { Ucenik } from '../models/ucenik';

@Injectable({
  providedIn: 'root'
})
export class UcenikService {

  constructor(private http : HttpClient) { }

  dohvatiUcenika(k : Korisnik) {
    return this.http.post<Ucenik>("http://localhost:4000/ucenik/dohvatiUcenika", {korIme: k.korIme, lozinka: k.lozinka})
  }



  izmeniIme(ki : string, ui : string) {
    return this.http.post<string>("http://localhost:4000/ucenik/izmeniIme", {korIme: ki, novoIme: ui})
  }

  izmeniPrezime(ki : string) {
    return this.http.post<string>("http://localhost:4000/ucenik/izmeniPrezime", {korIme: ki})
  }

  izmeniAdresu(ki : string) {
    return this.http.post<string>("http://localhost:4000/ucenik/izmeniAdresu", {korIme: ki})
  }

  izmeniTelefon(ki : string) {
    return this.http.post<string>("http://localhost:4000/ucenik/izmeniTelefon", {korIme: ki})
  }

  izmeniEmail(ki : string) {
    return this.http.post<string>("http://localhost:4000/ucenik/izmeniEmail", {korIme: ki})
  }

  izmeniSliku(ki : string) {
    return this.http.post<string>("http://localhost:4000/ucenik/izmeniSliku", {korIme: ki})
  }

  izmeniTipSkole(ki : string) {
    return this.http.post<string>("http://localhost:4000/ucenik/izmeniTipSkole", {korIme: ki})
  }

  izmeniRazred(ki : string) {
    return this.http.post<string>("http://localhost:4000/ucenik/izmeniRazred", {korIme: ki})
  }

}
