import { Component, OnInit, Predicate } from '@angular/core';
import {Chart, registerables} from 'chart.js';
import { UniversalService } from '../services/universal.service';
import { Router } from '@angular/router';
import { Predmet } from '../models/predmet';
import { Korisnik } from '../models/korisnik';
import { Nastavnik } from '../models/nastavnik';
import { Ucenik } from '../models/ucenik';
import { Cas } from '../models/cas';
import { Predlog } from '../models/predlog';


@Component({
  selector: 'app-admin',
  templateUrl: './admin.component.html',
  styleUrls: ['./admin.component.css']
})
export class AdminComponent implements OnInit {

  constructor(private universalService: UniversalService, private router: Router) {}

  korisnik : Korisnik = new Korisnik()

  sviPredmeti : Predmet[] = []
  sviPredmetiNaziv : string[] = []
  brojNastavnikaNaPredmetuArr : number[] = []

  sviNastavnici : Nastavnik[] = []
  polNastavnici : number[] = [0, 0]
  flagAktivanValues : number[] = [0, 0, 0]

  sviUcenici : Ucenik[] = []
  polUcenici : number[] = [0, 0]

  sviCasovi : Cas[] = []
  potvrda : number[] = [0, 0, 0] // po labelama: 0-odbijen, 1-prihvacen, 2-nije jos potvrdjen

  zahteviReg : Nastavnik[] = []

  noviPredmet : string = ""
  sviPredlozi : Predlog[] = []

  chartHistogram1 : any
  chartPie1 : any
  chartPie2 : any
  chartPie3 : any
  chartPie4 : any

  ngOnInit(): void {
    let k = localStorage.getItem("logovaniKorisnik")
    if (k != null) { 
      this.korisnik = JSON.parse(k)

      this.dohvatiSvePredmete()
      this.generisanjeHistograma()
      this.dohvatiSveNastavnike()
      this.generisanjePie1()
      this.dohvatiSveUcenike()
      this.generisanjePie2()
      this.dohvatiSveCasove()
      this.generisanjePie3()
      this.generisanjePie4()

      this.dohvatiZahteveNastavnika()
    }
  }

  refreshData() {
    this.chartHistogram1.destroy()
    this.chartPie1.destroy()
    this.chartPie2.destroy()
    this.chartPie3.destroy()
    this.chartPie4.destroy()

    this.polNastavnici = [0, 0]
    this.flagAktivanValues = [0, 0, 0]
    this.polUcenici = [0, 0]
    this.potvrda = [0, 0, 0]

    this.dohvatiSvePredmete()
    this.generisanjeHistograma()
    this.dohvatiSveNastavnike()
    this.generisanjePie1()
    this.dohvatiSveUcenike()
    this.generisanjePie2()
    this.dohvatiSveCasove()
    this.generisanjePie3()
    this.generisanjePie4()

    this.dohvatiZahteveNastavnika()
  }

  dodajPredmetAdmin() {
    let noviPredmetIndex = this.sviPredmeti.length;
    this.universalService.dodajPredmetAdmin(this.noviPredmet, noviPredmetIndex).subscribe(
      data => {
        if (data != null) {
          this.refreshData()
        }
        else {
          console.log("Greska. ")
        }
      }
    )
  }

  dohvatiSvePredloge() {
    this.universalService.dohvatiSvePredloge()
  }

  dohvatiZahteveNastavnika() {
    this.universalService.dohvatiZahteveNastavnika().subscribe(
      data => {
        if (data != null) {
          this.zahteviReg = data
        }
        else {
          console.log("Greska. ")
        }
      }
    )
  }

  prihvatiZahtev(n: Nastavnik) {
    this.universalService.prihvatiZahtev(n).subscribe(
      data => {
        if (data != null) {
          this.refreshData()
        }
        else {
          console.log("Greska. ")
        }
      }
    )
  }

  odbijZahtev(n: Nastavnik) {
    this.universalService.odbijZahtev(n).subscribe(
      data => {
        if (data != null) {
          this.refreshData()
        }
        else {
          console.log("Greska. ")
        }
      }
    )
  }

  dohvatiSveUcenike() {
    this.universalService.dohvatiSveUcenike().subscribe(
      data => {
        if (data != null) {
          this.sviUcenici = data

          for (let i = 0; i < this.sviUcenici.length; i++) {
            if (this.sviUcenici[i].pol == 'M') this.polUcenici[0]++;
            else this.polUcenici[1]++
          }
        }
        else {
          console.log("Greska. ")
        }
      }
    )
  }

  dohvatiSveNastavnike() {
    this.universalService.dohvSveNastavnike().subscribe(
      data => {
        if (data != null) {
          this.sviNastavnici = data

          for (let i = 0; i < this.sviNastavnici.length; i++) {
            if (this.sviNastavnici[i].pol == 'M') this.polNastavnici[0]++;
            else this.polNastavnici[1]++

            if (this.sviNastavnici[i].flagAktivan == 0) this.flagAktivanValues[0]++;
            else if (this.sviNastavnici[i].flagAktivan == 1) this.flagAktivanValues[1]++;
            else this.flagAktivanValues[2]++;
          }
        }
        else {
          console.log("Greska. ")
        }
      }
    )
  }

  dohvatiSveCasove() {
    this.universalService.dohvatiSveCasove().subscribe(
      data => {
        if (data != null) {
          this.sviCasovi = data

          for (let i = 0; i < this.sviCasovi.length; i++) {
            if (this.sviCasovi[i].flagPotvrda == 0) this.potvrda[0]++;
            else if (this.sviCasovi[i].flagPotvrda == 1) this.potvrda[1]++;
            else this.potvrda[2]++;
          }
        }
        else {
          console.log("Greska. ")
        }
      }
    )
  }

  dohvatiSvePredmete() {
    this.universalService.dohvSvePredmete().subscribe(
      data => {
        if (data != null) {
          this.sviPredmeti = data

          for (let i = 0; i < this.sviPredmeti.length; i++) {
            this.universalService.brojNastavnikaNaPredmetu(this.sviPredmeti[i].naziv).subscribe(
              data2 => {
                if (data2 != null) {
                  this.brojNastavnikaNaPredmetuArr[i] = data2
                  this.sviPredmetiNaziv[i] = this.sviPredmeti[i].naziv
                }
                else {
                  console.log("Greska. ")
                }
              }
            )
          }
        }
        else {
          console.log("Greska. ")
        }
      }
    )
  }

  generisanjeHistograma() {

    Chart.register(...registerables)

    this.chartHistogram1 = new Chart("histogramCanvas", {
      type: 'bar',
      data: {
        labels: this.sviPredmetiNaziv, 
        datasets: [{
          label: 'Broj nastavnika po predmetu',
          data: this.brojNastavnikaNaPredmetuArr,
          backgroundColor: 'rgba(255, 99, 132, 0.2)', 
          borderColor: 'rgba(255, 99, 132, 1)', 
          borderWidth: 1
        }]
      }, 
      options: {
        scales: {
          y: {            beginAtZero: true,          },
        }
      }
    });
  }

  generisanjePie1() {

    Chart.register(...registerables)

    this.chartPie1 = new Chart("pie1", {
      type: 'pie',
      data: {
        labels: ['M', 'Z'], 
        datasets: [{
          label: 'Raspodela nastavnika po polu',
          data: this.polNastavnici,
          backgroundColor: [
            'rgba(255, 99, 132, 0.2)',
            'rgba(54, 162, 235, 0.2)',
            'rgba(255, 206, 86, 0.2)',
            'rgba(75, 192, 192, 0.2)',
            'rgba(153, 102, 255, 0.2)',
          ],
          borderColor: [
            'rgba(255, 99, 132, 1)',
            'rgba(54, 162, 235, 1)',
            'rgba(255, 206, 86, 1)',
            'rgba(75, 192, 192, 1)',
            'rgba(153, 102, 255, 1)',
          ], 
          borderWidth: 1
        }]
      },
      options: {
        scales: {
          y: {            beginAtZero: true,          },
        }
      }
    });
  }


  generisanjePie2() {

    Chart.register(...registerables)

    this.chartPie2 = new Chart("pie2", {
      type: 'pie',
      data: {
        labels: ['M', 'Z'], 
        datasets: [{
          label: 'Raspodela ucenika po polu',
          data: this.polUcenici,
          backgroundColor: [
            'rgba(255, 99, 132, 0.2)',
            'rgba(54, 162, 235, 0.2)',
            'rgba(255, 206, 86, 0.2)',
            'rgba(75, 192, 192, 0.2)',
            'rgba(153, 102, 255, 0.2)',
          ],
          borderColor: [
            'rgba(255, 99, 132, 1)',
            'rgba(54, 162, 235, 1)',
            'rgba(255, 206, 86, 1)',
            'rgba(75, 192, 192, 1)',
            'rgba(153, 102, 255, 1)',
          ], 
          borderWidth: 1
        }]
      },
      options: {
        scales: {
          y: {            beginAtZero: true,          },
        }
      }
    });
  }

  generisanjePie3() {

    Chart.register(...registerables)

    this.chartPie3 = new Chart("pie3", {
      type: 'pie',
      data: {
        labels: ['odbijen', 'potvrdjen', 'nije razmotren'], 
        datasets: [{
          label: 'Potvrda casa',
          data: this.potvrda,
          backgroundColor: [
            'rgba(255, 99, 132, 0.2)',
            'rgba(54, 162, 235, 0.2)',
            'rgba(255, 206, 86, 0.2)',
            'rgba(75, 192, 192, 0.2)',
            'rgba(153, 102, 255, 0.2)',
          ],
          borderColor: [
            'rgba(255, 99, 132, 1)',
            'rgba(54, 162, 235, 1)',
            'rgba(255, 206, 86, 1)',
            'rgba(75, 192, 192, 1)',
            'rgba(153, 102, 255, 1)',
          ], 
          borderWidth: 1
        }]
      },
      options: {
        scales: {
          y: {            beginAtZero: true,          },
        }
      }
    });
  }

  generisanjePie4() {

    Chart.register(...registerables)

    this.chartPie4 = new Chart("pie4", {
      type: 'pie',
      data: {
        labels: ['odbijen', 'potvrdjen', 'nije razmotren'], 
        datasets: [{
          label: 'Potvrda nastavika',
          data: this.flagAktivanValues,
          backgroundColor: [
            'rgba(255, 99, 132, 0.2)',
            'rgba(54, 162, 235, 0.2)',
            'rgba(255, 206, 86, 0.2)',
            'rgba(75, 192, 192, 0.2)',
            'rgba(153, 102, 255, 0.2)',
          ],
          borderColor: [
            'rgba(255, 99, 132, 1)',
            'rgba(54, 162, 235, 1)',
            'rgba(255, 206, 86, 1)',
            'rgba(75, 192, 192, 1)',
            'rgba(153, 102, 255, 1)',
          ], 
          borderWidth: 1
        }]
      },
      options: {
        scales: {
          y: {            beginAtZero: true,          },
        }
      }
    });
  }

}
